import React from 'react';

const Hero = () => {
  return (
    <section className="bg-orange-50 py-20 text-center">
      <h1 className="text-5xl font-bold text-gray-800">Learn to Code with Coding Ninjas</h1>
      <p className="mt-4 text-lg text-gray-600">Join millions of learners to become job-ready.</p>
      <button className="mt-6 px-6 py-3 bg-orange-500 text-white rounded-lg hover:bg-orange-600">
        Explore Courses
      </button>
    </section>
  );
};

export default Hero;
